function initCheckboxAndRadioInput(a,b){document.querySelectorAll(a).forEach(function(a){if("bootstrap-checkbox"!==a.parentNode.className&&"bootstrap-radio"!==a.parentNode.className){var c=document.createElement("span");c.className=b,a.parentNode.insertBefore(c,a),c.appendChild(a),c.appendChild(document.createElement("span"))}})}initCheckboxAndRadioInput('input[type="checkbox"]',"bootstrap-checkbox"),initCheckboxAndRadioInput('input[type="radio"]',"bootstrap-radio");